mapper.update:
UPDATE 
   member
SET
   pw = #{spw}
WHERE
   id = #{id}
   AND NOT pw = #{spw}

mapper.select:
select
    count(*)
from
    point p, member m, code c
where
    id = #{id}
    AND m.mno = p.mno

    AND odate between #{startdate} and #{enddate}

    AND p.cno = c.cno

    AND c.upcno = #{supno}

SELECT pvalue, odate, body, upcno upno FROM ( SELECT rownum rno, pvalue, odate, body, upcno FROM ( SELECT pno, cno, pvalue, to_char(odate, 'YY-MM-DD') odate FROM POINT p, MEMBER m WHERE id = #{myVO.id} AND p.mno = m.mno
AND odate between #{myVO.startdate} and #{myVO.enddate}
) s, CODE c WHERE s.cno = c.cno
AND upcno = #{myVO.supno}
ORDER BY pno desc ) WHERE rno BETWEEN #{page.startCont} AND #{page.endCont}
